
const delay=400
window.addEventListener('DOMContentLoaded',function(){const menu=document.querySelector('.header__menu')
const header=document.querySelector('.header')
let lock=false
const burger=document.querySelector('.header__burger')
if(burger){burger.addEventListener('click',function(){if(lock)return
lock=true
toggleMenu()
setTimeout(()=>{lock=false},delay)})
function toggleMenu(){menu.classList.toggle('active')
lockBody()}
const menuBurger=document.querySelector('.menu__burger')
menuBurger.addEventListener('click',function(){if(lock)return
lock=true
toggleMenu()
setTimeout(()=>{lock=false},delay)})
window.addEventListener('click',function(e){if(document.querySelector('.menu.active')&&!e.target.closest('.menu')&&!e.target.closest('.burger')){document.querySelector('.menu.active').classList.remove('active')
lockBody()}})}
const bannerMore=document.querySelector('.banner__more')
if(bannerMore){bannerMore.addEventListener('click',function(){const menuBanner=document.querySelector('.menu-banner')
menuBanner.classList.add('active')
lockBody()})
closeMenuBanner=document.querySelector('.menu-banner__close')
closeMenuBanner.addEventListener('click',function(){const menuBanner=document.querySelector('.menu-banner')
menuBanner.classList.remove('active')
lockBody()})}
function lockBody(){const scrollWidth=window.innerWidth-document.body.clientWidth
if(scrollWidth===0&&document.body.classList.contains('lock')){document.body.classList.remove('lock')
document.body.style.paddingRight='0px'
return}
document.body.classList.toggle('lock')
document.body.style.paddingRight=scrollWidth+'px'}
if(document.querySelector('.actions-navigation')){document.querySelector('.block-navigation__content .ss-wrapper').appendChild(document.querySelector('.actions-navigation'))}
if(document.querySelector('.actions-navigation__top')){document.querySelector('.actions-navigation__top').addEventListener('click',function(){document.querySelector('.content-navigation .ss-content').scrollTop=0})}
if(document.querySelector('.navigation__buttons')){const navigationButtons=new Swiper('.navigation__buttons',{spaceBetween:10,slidesPerView:1,loop:true,speed:1000,breakpoints:{575.98:{loop:false,slidesPerView:'auto',}},navigation:{nextEl:'.navigation__button-next',prevEl:'.navigation__button-prev'},})}
if(document.querySelector('.live__experts')){const liveSlider=new Swiper('.live__experts',{spaceBetween:10,slidesPerView:1,loop:true,speed:1000,breakpoints:{635.98:{slidesPerView:2,},},navigation:{nextEl:'.live__button-next',prevEl:'.live__button-prev'},})}
if(document.querySelector('.base-side__items')){const baseSideItems=new Swiper('.base-side__items',{spaceBetween:10,slidesPerView:1,loop:true,speed:1000,breakpoints:{635.98:{slidesPerView:2,},},navigation:{nextEl:'.base-side__button-next',prevEl:'.base-side__button-prev'},})}
if(document.querySelector('.news')){const newsMasonry=new Masonry(document.querySelector('.news__items'),{itemSelector:'.item-news',percentPosition:true,});}
if(document.querySelector('.profile')){window.addEventListener('click',function(e){if(e.target.closest('.profile__phone')){const el=document.createElement('div')
el.className='profile__item'
el.innerHTML=`
                   <div class="profile__item">
                        <div class="profile__label">Телефон</div>
                        <div class="profile__input input-wrapper">
                            <input class="input" type="text">
                        </div>
                    </div>
                `
document.querySelector('.profile__phones').appendChild(el)}})}})